<script>
    import {mapMutations,mapState,mapGetters} from 'vuex';
	import tPayWay from "@/components/t-pay-way/ti-pay-way"
	export default {
		components: {
			tPayWay
		},
        computed:{
            ...mapState(['modalAction']),
        },
		data() {
			return {
				goods: '',
				listData: '',
                order: '',
                items: [{title: "提交申请",},{title: "处理中",},{title: "申请成功",}],
                activeSteps:1,
                refund:'',
			}
		},
		onLoad(){
		    this.getList()
		},
		methods: {
		    getList:function () {
		        if(!this.$Route.query.id){
		            this.$utils.toast('参数错误')
					setTimeout(()=>{this.$Router.back()},1500)
					return;
				}
				this.$http.get('order.refund',{id:this.$Route.query.id}).then((res)=>{
					this.goods = res.goods
					this.order = res.order
					this.refund = res.refund
					this.activeSteps = parseInt(res.refund.status)+1
				})
            },
			submit:function () {
		        if(this.pickerIndex<0){
		            this.$utils.toast('请选择申请类型')
					return;
				}
				this.$http.get('order.refund.submit',{id:this.order.id,price:this.order.price,rtype:this.pickerIndex}).then((res)=>{
		            
				})
            },
            pickerCallback:function (e) {
		        if(e.detail.value<0) e.detail.value = 0
                this.pickerIndex = e.detail.value;
            },
            reasonCallback:function (e) {
                if(e.detail.value<0) e.detail.value = 0
				this.reasonIndex = e.detail.value;
            }

		}
	}
</script>

